	/**
	 * 将WADE3.0的串{X_TRANS_TYPE=[\"HTTP\"], TRADE_EPARCHY_CODE=[[\"0898\"], [\"0899\"]]}转换成Map对象
	 * @param value
	 * @return
	 */
	@SuppressWarnings({ "rawtypes", "unchecked"})
	public static Map strToMap(String value) {
		Map map = new HashMap();
		map.putAll((Map) strToList(value).get(0));
		return map;
	}
	
	/**
	 * 将WADE3.0的串{X_TRANS_TYPE=[\"HTTP\"], TRADE_EPARCHY_CODE=[[\"0898\"], [\"0899\"]]}转换成List对象,List每个元素为一个Map
	 * @param value
	 * @return List
	 */
	@SuppressWarnings("rawtypes")
	public static List strToList(String value) {
		if (value == null) return null;
		
		Pattern pattern = Pattern.compile("([\\[|\\{]+\")");
		Matcher matcher = pattern.matcher(value);
		boolean iswadestr = !(matcher.find() && value.startsWith(matcher.group()));
		
		StringBuffer str = new StringBuffer();
		String regstr = iswadestr ? "(\\r)|(\\n)|(\\$)|(\\\\)|(\", \")|([\\[(, )]?\"[\\](, )]?)" : "(\\r)|(\\n)|(\\$)|(\\\\)|([\\{,]\"\\d*\\w*[:]?\\w*\":)";
		pattern = Pattern.compile(regstr);
		matcher = pattern.matcher(value);
		while (matcher.find()) {
			String group = matcher.group();
			if ("\r".equals(group)) {
				matcher.appendReplacement(str, "!~5~!");
			} else if ("\n".equals(group)) {
				matcher.appendReplacement(str, "!~6~!");
			} else if ("$".equals(group)) {
				matcher.appendReplacement(str, "!~7~!");
			} else if ("\"".equals(group)) {
				matcher.appendReplacement(str, "!~8~!");
			} else if ("\\".equals(group)) {
				matcher.appendReplacement(str, "!~9~!");
			} else if (!iswadestr) {
				if ((group.startsWith("{\"") || group.startsWith(",\"")) && group.endsWith("\":")) {
					matcher.appendReplacement(str, group.replaceAll("\"", "!~a~!"));
				}
			}
		}
		matcher.appendTail(str);
		
		value = str.toString();
		str = new StringBuffer();
		pattern = Pattern.compile(iswadestr ? "(\".*?\"[\\],])" : "(\".*?\"[\\]\\},])");
		matcher = pattern.matcher(value);
		while (matcher.find()) {
			String group = matcher.group();
			String prefix = group.substring(0, 1);
			String suffix = group.substring(group.length() - 2);
			group = group.substring(1, group.length() - 2);
			
			StringBuffer substr = new StringBuffer();
			Pattern subpattern = Pattern.compile("(\\{)|(\\[)|(\\])|(,)|(\")");
			Matcher submatcher = subpattern.matcher(group);
			while (submatcher.find()) {
				String subgroup = submatcher.group();
				if ("{".equals(subgroup)) {
					submatcher.appendReplacement(substr, "!~1~!");
				} else if ("[".equals(subgroup)) {
					submatcher.appendReplacement(substr, "!~2~!");
				} else if ("]".equals(subgroup)) {
					submatcher.appendReplacement(substr, "!~3~!");
				} else if (",".equals(subgroup)) {
					submatcher.appendReplacement(substr, "!~4~!");
				} else if ("\"".equals(subgroup)) {
					submatcher.appendReplacement(substr, "!~8~!");
				}
			}
			submatcher.appendTail(substr);
			matcher.appendReplacement(str, prefix + substr + suffix);
		}
		matcher.appendTail(str);
		
		if (iswadestr) {
			value = str.toString();
			str = new StringBuffer();
			pattern = Pattern.compile("(=?[\\{\\[][\\{\\}\\[\\]]*(, [\"]?)?[\\{\\}\\[]*)|([\\}\\]]*(, [\"]?)[\\{\\[]*)|(\", \")");
			matcher = pattern.matcher(value);
			while (matcher.find()) {
				String group = matcher.group();
				if (group.startsWith("=")) {
					group = "\":" + group.substring(1);
				}
				if (group.endsWith("{")) {
					group += "\"";
				} else if (group.endsWith(" ")) {
					group += "\"";
				}
				group = group.replaceFirst(" ", "");
				matcher.appendReplacement(str, group);
			}
			matcher.appendTail(str);
			
			value = str.toString();
			str = new StringBuffer();
			pattern = Pattern.compile("(:\\[\".*?\"\\])");
			matcher = pattern.matcher(value);
			while (matcher.find()) {
				String group = matcher.group();
				if (!Pattern.compile("(\",\")|(\",\\[)").matcher(group).find()) {
					matcher.appendReplacement(str, group.substring(0, 1) + group.substring(2, group.length() - 1));	
				}
			}
			matcher.appendTail(str);
		}
		
		value = str.toString();
		str = new StringBuffer();
		pattern = Pattern.compile("(!~1~!)|(!~2~!)|(!~3~!)|(!~4~!)|(!~5~!)|(!~6~!)|(!~7~!)|(!~8~!)|(!~9~!)|(!~a~!)");
		matcher = pattern.matcher(value);
		while (matcher.find()) {
			String group = matcher.group();
			if ("!~1~!".equals(group)) {
				matcher.appendReplacement(str, "{");
			} else if ("!~2~!".equals(group)) {
				matcher.appendReplacement(str, "[");
			} else if ("!~3~!".equals(group)) {
				matcher.appendReplacement(str, "]");
			} else if ("!~4~!".equals(group)) {
				matcher.appendReplacement(str, ",");
			} else if ("!~5~!".equals(group)) {
				matcher.appendReplacement(str, "\\\\r");
			} else if ("!~6~!".equals(group)) {
				matcher.appendReplacement(str, "\\\\n");
			} else if ("!~7~!".equals(group)) {
				matcher.appendReplacement(str, "\\$");
			} else if ("!~8~!".equals(group)) {
				matcher.appendReplacement(str, "\\\\\"");
			} else if ("!~9~!".equals(group)) {
				matcher.appendReplacement(str, "\\\\\\\\");
			} else if (!iswadestr) {
				if ("!~a~!".equals(group)) {
					matcher.appendReplacement(str, "\"");
				}
			}
		}
		matcher.appendTail(str);
		value = str.toString();
		
		if (!(value.startsWith("[") && value.endsWith("]"))) {
			value = "[" + value + "]";
		}
		
		try {
			return (List) JSONSerializer.toJSON(value);
		} catch (JSONException e) {
			throw new RuntimeException("syntax error" + e.getMessage(), e);
		}
	}

	
	/**
	 * toWade3String
	 * @param map
	 * @return
	 */
	@SuppressWarnings({ "rawtypes", "unchecked" })
	public static String toWadeString(Map map) {
    	StringBuffer str = new StringBuffer();
    	str.append("{");
    	
    	Iterator it = map.entrySet().iterator();
    	while (it.hasNext()) {
    		Entry entity = (Entry) (it.next());
    		Object key = entity.getKey();
    		Object value = entity.getValue();
    		str.append(key + "=");
    		
    		if (value == null) {
    			str.append("[\"\"]");
    		} else if ( DataAdapter.isWadeJSONObjectInstance(value) ) {
    			Map object = new HashMap();
    			
    			//不知道为什么这里json变量没用到，先注释掉 xiedx 2017/3/4
    			//com.ailk.common.json.JSONObject json = (com.ailk.common.json.JSONObject) value;	
    			
    			str.append(toWadeString(object));
      		} else if (value instanceof JSONObject) {
    			Map object = new HashMap();
    			object.putAll((Map) value);
    			str.append(toWadeString(object));
      		} else if (value instanceof JSONArray) {
    			List object = new ArrayList();
    			object.addAll((List) value);
    			str.append(toWadeString(object));
    		} else if (value instanceof Map) {
        		str.append(toWadeString((Map)value));
    		} else if (value instanceof List) {
        		str.append(toWadeString((List) value));
    		} else {
    			str.append("[\"" + value + "\"]");
    		}
    		
    		if (it.hasNext()) str.append(", ");
    	}
    	
    	str.append("}");
    	return str.toString();
	}
	
	public static String toWadeString(List list) {
		return toWadeString(list, false);
	}
	
	
	/**
	 * toWade3String
	 * @param list
	 * @return
	 */
	@SuppressWarnings({ "rawtypes", "unchecked" })
	public static String toWadeString(List list, boolean toMap) {
    	StringBuffer str = new StringBuffer();
    	
    	if (toMap) {
    		//str.append("{");
    	} else {
    		str.append("[");
    	}
    	
    	Iterator it = list.iterator();
    	while (it.hasNext()) {
    		Object value = it.next();
    		if (value == null) {
    			str.append("\"\"");
    		} else if (value instanceof JSONObject) {
    			Map object = new HashMap();
    			object.putAll((Map) value);
    			str.append(toWadeString(object));
      		} else if (value instanceof JSONArray) {
    			List object = new ArrayList();
    			object.addAll((List) value);
    			str.append(toWadeString(object));
    		} else if (value instanceof Map) {
    			str.append(toWadeString((Map) value));
    		} else if (value instanceof List) {
    			str.append(toWadeString((List) value));
    		} else {
    			str.append("\"" + value + "\"");
    		}
    		if (it.hasNext()) str.append(", ");
    	}
    	
    	if (toMap) {
    		//str.append("}");
    	} else {
    		str.append("]");
    	}
    	return str.toString();
	}
	
	
	/**
	 * 将WADE3的IDataset转成WADE4的IDataset，仅适应于WADE4调WADE3的服务时
	 * @param list
	 * @return
	 */
	public static IDataset wade3To4Dataset(List list) {
		IDataset source = new DatasetList();
		
		for (Object obj : list) {
			if (obj instanceof JSONArray) {
				IDataset sub = jsonArrayToDataset((JSONArray) obj);
				source.add(sub);
			} else if (obj instanceof JSONObject) {
				source.add(jsonObjectToDataMap((JSONObject) obj));
			} else if ( DataAdapter.isWadeJSONArrayInstance(obj) ) { //使用反射方法判断类型  xiedx 2017/3/4
				//source.add(DatasetList.fromJSONArray((com.ailk.common.json.JSONArray) obj));
				//使用反射方法构造兼容两种版本的DatasetList  xiedx 2017/3/4
				source.add( DataAdapter.fromWadeJSONArray(obj) ) ; 
			} else if ( DataAdapter.isWadeJSONObjectInstance(obj) ) { //使用反射方法判断类型  xiedx 2017/3/4
				//source.add(DataMap.fromJSONObject((com.ailk.common.json.JSONObject)obj));
				//使用反射方法构造兼容两种版本的DataMap   xiedx 2017/3/4
				source.add( DataAdapter.fromWadeJSONObject(obj) ); 
			} else {
				source.add(obj);
			}
		}
		
		return source;
	}
	
	public static IData wade3To4DataMap(Map map) {
		IData source = new DataMap();
		
		Iterator iter = map.keySet().iterator();
		while (iter.hasNext()) {
			String key = (String) iter.next();
			Object obj = map.get(key);
			
			if (obj instanceof JSONArray) {
				source.put(key, jsonArrayToDataset((JSONArray) obj));
			} else if (obj instanceof JSONObject) {
				source.put(key, jsonObjectToDataMap((JSONObject) obj));
			} else if ( DataAdapter.isWadeJSONArrayInstance(obj) ) { //使用反射方法判断类型  xiedx 2017/3/4
				//source.put(key, DatasetList.fromJSONArray((com.ailk.common.json.JSONArray)obj));
				//使用反射方法构造兼容两种版本的DatasetList  xiedx 2017/3/4
				source.put( key, DataAdapter.fromWadeJSONArray(obj) );
			} else if ( DataAdapter.isWadeJSONObjectInstance(obj) ) { //使用反射方法判断类型  xiedx 2017/3/4
				//source.put(key, DataMap.fromJSONObject((com.ailk.common.json.JSONObject) obj));
				//使用反射方法构造兼容两种版本的DataMap   xiedx 2017/3/4
				source.put( key, DataAdapter.fromWadeJSONObject(obj) );
			} else {
				source.put(key, obj);
			}
		}
		
		return source;
	}
	
	private static IDataset jsonArrayToDataset(JSONArray jsonArray) {
		IDataset source = new DatasetList();
		
		for (Object obj : jsonArray) {
			if (obj instanceof JSONArray) {
				IDataset sub = jsonArrayToDataset((JSONArray) obj);
				source.add(sub);
			} else if (obj instanceof JSONObject) {
				source.add(jsonObjectToDataMap((JSONObject) obj));
			} else {
				source.add(obj);
			}
		}
		return source;
	}
	
	private static IData jsonObjectToDataMap(JSONObject jsonObject) {
		IData source = new DataMap();
		
		if (null == jsonObject || jsonObject.isNullObject())
			return source;
		
		Iterator iter = jsonObject.keys();
		while (iter.hasNext()) {
			String key = (String) iter.next();
			Object obj = jsonObject.get(key);
			
			if (obj instanceof JSONArray) {
				source.put(key, jsonArrayToDataset((JSONArray) obj));
			} else if (obj instanceof JSONObject) {
				source.put(key, jsonObjectToDataMap((JSONObject) obj));
			} else {
				source.put(key, obj);
			}
		}
		
		return source;
	}
	
